<?php
/**
 * https://leetcode.com/problems/word-search/
 * 
 * 22:23 - 23:10
 */

class Solution {
    /** 
     * @param String[][] $board
     * @param String $word
     * @return Boolean
     */
    function exist($board, $word) {
        for ($i = 0; $i < count($board); $i++) {
            for ($j = 0; $j < count($board[$i]); $j++) {
                if (substr($word, 0, 1) == $board[$i][$j]) {
                    $result = $this->dfs($i, $j, $word, $board);
                    if ($result) return true;
                }
            }
        }
        
        return false;
    }

    function dfs($i, $j, $word, $board) {
        if (!isset($board[$i][$j]) || strlen($word)==0 || $word[0] != $board[$i][$j]) {
            return false;
        }

        if ($word == $board[$i][$j]) return true;

        $board[$i][$j] = ' ';
        $word = substr($word, 1);
        
        return $this->dfs($i + 1, $j, $word, $board)
            || $this->dfs($i, $j + 1, $word, $board)
            || $this->dfs($i - 1, $j, $word, $board)
            || $this->dfs($i, $j - 1, $word, $board);
    }
}


